import React from 'react';
import { confirmAlert } from 'react-confirm-alert'; // Import
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import css
//import Popup from './Popup';



const Affichage = props => (
	<table className="table table-striped table-bordered">
		<thead className="thead-dark">
			<tr>
                <th>ID</th>
                <th>Nom</th>
                <th>Prénom</th>
                <th>Action</th>
			</tr>
		</thead>
		<tbody>
			{props.users.length > 0 ? (

				props.users.map(user => (

					<tr key={user.id}>
                        <td>{user.id-1}</td>
						<td>{user.nom}</td>
						<td>{user.prenom}</td>
						<td>
                        <div className='custom-ui'></div>
							<button  onClick={Confirm(props.deleteUser(user.id))} className="btn btn-danger">X</button>
						</td>
					</tr>
                ))
			) : (
				<tr>
					<td colSpan={3}>Aucun(0) apprenant enregistré</td>
				</tr>
			)}
		</tbody>
	</table>
)
//Confirm(props.deleteUser(user.id))
function Confirm(callback){
    let alerte = confirmAlert({
      //submit = () => {
        customUI: ({ onClose }) => {
          //return (
            <div className='custom-ui'>
              <p>Confirmer la suppression</p>
              <button onClick={onClose}>Non</button>
              <button onClick={callback}>Oui</button>
            </div>
          //)
        }
      //}
    //) )   
  })
    return alerte
}


export default Affichage
